function Kick(osc, volEnv, pitchEnv, pitchRange, baseFrequency) {

  this.osc = osc;
  this.osc.gate(true);

  this.pitchEnv = pitchEnv;
  this.volEnv = volEnv;

  this._gate = false;

  this.gate = function(gate) {
    return arguments.length ? (this._gate = gate, this.pitchEnv.gate(gate),
      this.volEnv.gate(gate)) : this._gate;
  };

  this.kick = function() {
    // reset envelopes
    this.pitchEnv.phase = AdsrPhase.ATTACK;
    this.volEnv.phase = AdsrPhase.ATTACK;
    this.gate(true);
  }

  this.pitchRange = typeof pitchRange !== 'undefined' ? pitchRange : 2;
  this.baseFrequency = typeof baseFrequency !== 'undefined' ? baseFrequency :
    49; // G-1

}

Kick.prototype.getNextSample = function() {
  this.volEnv.advance();
  this.pitchEnv.advance();
  osc.frequency = (this.pitchEnv.gain * this.pitchRange + 1) * this.baseFrequency;
  return this.volEnv.gain * osc.getNextSample();
}